import React from "react";
import styled from "styled-components";
import Info from "../../PageComponents/Info";
import Layout from "../../components/Layout";

const Item = ({
  name,
  category,
  taste,
  producer,
  productionArea,
  price,
  url,
  packageImg,
}) => (
  <Root>
    <Layout>
      <WineImage src={packageimg} alt={name} />

      <Info
        name={name}
        category={category}
        taste={taste}
        producer={producer}
        productionArea={productionArea}
        price={price}
        url={url}
      />
    </Layout>
  </Root>
);

export default Item;

