import React from "react";
import Header from "../../components/Header";
import WineBottle from "../../PageComponents/WineBottle";
import Footer from "../../components/Footer";
import styled from "styled-components";

const contentful = require("contentful");

Index.getInitialProps = async () => {
  const space = "ch9w92me3811";
  const accessToken = "BNy24JRP1S43ycOl1B90rJaPnRxSlAoQOCm_TBnyi_Q";

  const client = contentful.createClient({ space, accessToken });

  const entryId = "7kbsF76DM4UZUtl2F9cT3S";
  const entry = await client.getEntry(entryId);
  return {
    id: entry.fields.id,
    name: entry.fields.name,
    category: entry.fields.category,
    taste: entry.fields.taste,
    producer: entry.fields.producer,
    productionArea: entry.fields.productionArea,
    price: entry.fields.price,
    url: entry.fields.url,
  };
};

const Index = () => (
  <Root>
    <Header />

    <Bottles>
      {Index.map(
        ({ id, date, name, category, producer, productionArea }, i) => (
          <WineBottle
            key={`wine-bottle-${i}`}
            id={id}
            date={date}
            name={name}
            category={category}
            producer={producer}
            productionArea={productionArea}
          />
        )
      )}
    </Bottles>

    <Footer />
  </Root>
);

const Root = styled.div`
  margin-left: auto;
  margin-right: auto;
  background: beige;
  font-family: YuMincho;
  letter-spacing: 3;
  font-weight: 300;
  color: lightgray;
`;

const Bottles = styled.div`
  display: flex;
  flex-wrap: wrap;
`;

export default Index;
