import React from "react";

const displayStyle = {
  display: "grid",
};

const wineBottleStyle = {
  width: 100,
  height: "auto",
  marginLeft: 30,
};

const soilStyle = {
  width: 150,
  height: 300,
  backgroundColor: "burlywood",
  borderRadius: 10,
};

function Winebottle() {
  return (
    <div style={displayStyle}>
      <img
        style={wineBottleStyle}
        src="./wineBottleIcon.svg"
        alt="ワインボトル"
      />
      <div style={soilStyle} />
    </div>
  );
}

export default Winebottle;
